mapper.select:
UPDATE	SPRING_BOARD_TBL
SET VIEWCNT = NVL(VIEWCNT, 0) + 1
WHERE SEQ = #{ seq }

SELECT	 SEQ, TITLE, CONTENT, WRITER, TO_CHAR(REGDATE, 'RRRR"년" MM"월" DD"일" MI"분" SS"초"') AS REGDATE, VIEWCNT
FROM	 SPRING_BOARD_TBL
WHERE	 SEQ = #{ seq }

UPDATE 	SPRING_BOARD_TBL
SET	 	TITLE = #{ title }, CONTENT=#{ content }
WHERE	SEQ = #{ seq }
